import 'dart:developer';import 'package:flutter/material.dart';import 'package:zen_player/modules/home/Home.dart';import 'package:zen_player/modules/setting/Setting.dart';class BottomNavigation extends StatefulWidget {  @override  State<StatefulWidget> createState() {    return _BottomNaviagtionState();  }}class _BottomNaviagtionState extends State<BottomNavigation> {  int _selectedIndex = 0;  static List<Widget> _widgets = [Home(), Setting()];  void _onItemTapped(int index, BuildContext context) {    setState(() {      _selectedIndex = index;    });    // Routes.changeRoute(_widgets[index], context);  }  @override  Widget build(BuildContext context) {    log("current index ${_selectedIndex.toString()}");    return BottomNavigationBar(      items: const <BottomNavigationBarItem>[        BottomNavigationBarItem(icon: Icon(Icons.home), label: "Home"),        BottomNavigationBarItem(icon: Icon(Icons.settings), label: "Settings")      ],      currentIndex: _selectedIndex,      onTap: (int index) => _onItemTapped(index, context),    );  }}class DefaultLayout extends StatelessWidget {  final String appTitle = "Zen Player";  final Widget child;  final AppBar? appBar;  DefaultLayout({Key? key, required this.child, this.appBar});  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: this.appBar ??          AppBar(            title: Text(appTitle),          ),      body: this.child,      bottomNavigationBar: BottomNavigation(),    );  }}