import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:zen_player/utils/routes.dart';class SearchButton extends StatelessWidget {  final bool replaceRoute;  SearchButton({this.replaceRoute = false});  @override  Widget build(BuildContext context) {    return IconButton(        padding: EdgeInsets.symmetric(vertical: 20),        icon: const Icon(Icons.search),        onPressed: () {          if (replaceRoute) {            Navigator.pushReplacementNamed(context, Routes.search);          } else {            Navigator.pushNamed(context, Routes.search);          }        });  }}