import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:flutter_redux/flutter_redux.dart';import 'package:redux/redux.dart';import 'package:zen_player/redux/states/app.dart';class _ViewModel {  List<String> searchHistory = [];  _ViewModel({required this.searchHistory});  factory _ViewModel.fromStore(Store<AppState> store) {    return _ViewModel(searchHistory: store.state.searchHistory);  }}class SearchHistory extends StatelessWidget {  @override  Widget build(BuildContext context) {    return StoreConnector(        builder: (context, _ViewModel vm) => ListView.builder(            itemBuilder: (context, idx) {              return ListTile(                title: Text(vm.searchHistory[idx]),              );            },            itemCount: vm.searchHistory.length),        converter: (Store<AppState> store) => _ViewModel.fromStore(store));  }}