import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:zen_player/layout/Default.dart';class SearchPage extends StatefulWidget {  @override  State<StatefulWidget> createState() {    return _SearchPageState();  }}class _SearchPageState extends State<SearchPage> {  String searchStr = "";  void _onStrChanged(String str) {    setState(() {      searchStr = str;    });  }  @override  Widget build(BuildContext context) {    return DefaultLayout(        appBar: AppBar(          title: TextField(            onChanged: _onStrChanged,            decoration: InputDecoration(                hintText: "Search by video names or urls",                // contentPadding: EdgeInsets.symmetric(vertical: 18),                isDense: true),          ),        ),        child: Center(          child: Text("Search page"),        ));  }}