import 'dart:developer';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:zen_player/modules/vendors/video.dart';import 'package:zen_player/utils/config.dart';class _UploaderSection extends StatelessWidget {  final AppVideo video;  _UploaderSection({required this.video});  @override  Widget build(BuildContext context) {    return Container(      padding: EdgeInsets.symmetric(vertical: 4),      child: Row(        children: [          Icon(Icons.person),          SizedBox(            width: 12,          ),          Expanded(child: Text(video.uploader ?? "No uploader"))        ],      ),    );  }}class _TitleSection extends StatelessWidget {  final bool expanded;  final String title;  _TitleSection({    required this.expanded,    required this.title,  });  @override  Widget build(BuildContext context) {    return Row(      crossAxisAlignment: CrossAxisAlignment.start,      children: [        Expanded(            child: Text(          title,          style: Theme.of(context).textTheme.headline6,        )),        Icon(expanded ? Icons.arrow_drop_up : Icons.arrow_drop_down)      ],    );  }}class VideoDescriptionSection extends StatefulWidget {  final AppVideo video;  VideoDescriptionSection({required this.video});  @override  State<StatefulWidget> createState() {    return _VideoDescriptionState();  }}class _VideoDescriptionState extends State<VideoDescriptionSection> {  bool isExpanded = false;  _onToggleExpanded(bool expanded) {    setState(() {      isExpanded = expanded;    });  }  @override  Widget build(BuildContext context) {    return Container(      padding: EdgeInsets.symmetric(horizontal: 8),      child: Column(        crossAxisAlignment: CrossAxisAlignment.start,        children: [          InkWell(            onTap: () {              _onToggleExpanded(!isExpanded);            },            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: [                _TitleSection(                  expanded: isExpanded,                  title: widget.video.title,                ),                SizedBox(                  height: 8,                ),                Text(                  widget.video.publishedAtStr ?? "No published date",                  style: Theme.of(context).textTheme.subtitle1,                ),              ],            ),          ),          Divider(            color: Theme.of(context).colorScheme.secondary,          ),          _UploaderSection(video: widget.video),          Divider(            color: Theme.of(context).colorScheme.secondary,          ),          SingleChildScrollView(              child: InkWell(            onTap: () {              setState(() {                isExpanded = !isExpanded;              });            },            child: Text(              widget.video.description ?? "No description",              overflow: isExpanded ? null : TextOverflow.ellipsis,              maxLines: isExpanded ? null : 1,              style: Theme.of(context).textTheme.bodyText2,            ),          )),        ],      ),    );  }}