import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:zen_player/modules/vendors/video.dart';import 'package:zen_player/modules/video_detail/players/player_factory.dart';import 'package:zen_player/modules/video_detail/players/youtube/IframePlayer.dart';class VideoPage extends StatefulWidget {  @override  State<StatefulWidget> createState() {    return _VideoPageState();  }}class _VideoPageState extends State<VideoPage> {  @override  Widget build(BuildContext context) {    final AppVideo? video =        ModalRoute.of(context)!.settings.arguments as AppVideo;    if (video == null) {      Navigator.pop(context);      return Container();    }    return Scaffold(      resizeToAvoidBottomInset: false,      appBar: AppBar(        title: Text(          video.title,          style: Theme.of(context).textTheme.headline6!,        ),      ),      body: LayoutBuilder(        builder: (context, BoxConstraints constraints) {          return ListView(            shrinkWrap: true,            children: [              ConstrainedBox(                constraints: BoxConstraints(maxHeight: constraints.maxHeight),                child: PlayerFactory.generatePlayer(video: video),              ),              Text(video.title),              SizedBox(                height: 12,              ),              Container(                padding: EdgeInsets.symmetric(vertical: 12),                child: Text(video.description ?? "Unknown"),              ),            ],          );        },      ),    );  }}class VideoPageContainer extends StatelessWidget {  @override  Widget build(BuildContext context) {    return VideoPage();  }}