import 'package:flutter/cupertino.dart';import 'package:zen_player/modules/vendors/video.dart';import 'package:zen_player/modules/video_detail/players/youtube/IframePlayer.dart';import 'package:zen_player/modules/video_detail/players/youtube/MobilePlayer.dart';class PlayerFactory {  static Widget generatePlayer(AppVideo video) {    switch (video.provider) {      case AppVideoProvider.YouTube:        return YouTubeIframePlayer(video: video);      default:        return YouTubeIframePlayer(video: video);    }  }}