import 'dart:developer';import 'package:flutter/cupertino.dart';import 'package:youtube_player_flutter/youtube_player_flutter.dart';import 'package:zen_player/modules/vendors/video.dart';class YouTubeMobilePlayer extends StatefulWidget {  final AppVideo video;  YouTubeMobilePlayer({required this.video});  @override  State<StatefulWidget> createState() {    return _YouTubeMobilePlayerState();  }}class _YouTubeMobilePlayerState extends State<YouTubeMobilePlayer> {  late YoutubePlayerController _controller;  @override  void initState() {    super.initState();    _controller = YoutubePlayerController(        initialVideoId: widget.video.id,        flags: YoutubePlayerFlags(autoPlay: true));  }  @override  Widget build(BuildContext context) {    return YoutubePlayer(      controller: _controller,      showVideoProgressIndicator: true,      onReady: () {        log("YouTube player ready.");      },    );  }}