import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:zen_player/modules/vendors/video.dart';class VideoCard extends StatelessWidget {  final Key? key;  final Video video;  VideoCard({this.key, required this.video}) : super(key: key);  @override  Widget build(BuildContext context) {    return Card(      child: Row(        children: [          Image.network(            video.thumbnail ?? "http://www.google.com",            width: 140,          ),          Expanded(              child: Container(            padding: EdgeInsets.all(8),            child: Column(              crossAxisAlignment: CrossAxisAlignment.start,              children: [                Tooltip(                  message: video.title,                  child: Text(                    video.title,                    style: Theme.of(context)                        .textTheme                        .bodyText1!                        .copyWith(overflow: TextOverflow.ellipsis),                    softWrap: true,                  ),                ),                SizedBox(                  height: 4,                ),                Tooltip(                  message: video.description,                  child: Text(                    video.description,                    maxLines: 2,                    style: Theme.of(context).textTheme.subtitle1,                  ),                ),                SizedBox(                  height: 2,                ),                Text(                  video.pulishedAtStr,                  style: Theme.of(context).textTheme.subtitle2,                )              ],            ),          ))        ],      ),    );  }}