import 'package:flutter/cupertino.dart';import 'package:zen_player/modules/vendors/video.dart';class VideoCard extends StatelessWidget {  final Key? key;  final Video video;  VideoCard({this.key, required this.video}) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(      child: Row(        children: [          Image.network(            video.thumbnail ?? "http://www.google.com",            width: 140,          ),          Column(            children: [              Text(                "Title ${video.title}",                style: TextStyle(                  overflow: TextOverflow.ellipsis,                ),                softWrap: true,              )            ],          )        ],      ),    );  }}