import 'package:flutter/cupertino.dart';import 'package:zen_player/modules/vendors/video.dart';import 'package:zen_player/modules/videos_list/VideoCard.dart';class VideosList extends StatefulWidget {  final List<Video> videos;  VideosList({required this.videos});  @override  State<StatefulWidget> createState() {    return _VidoesListState();  }}class _VidoesListState extends State<VideosList> {  @override  Widget build(BuildContext context) {    return Container(      child: ListView.builder(        itemBuilder: (context, int idx) {          Video video = widget.videos[idx];          return VideoCard(            video: video,          );        },        itemCount: widget.videos.length,        padding: EdgeInsets.symmetric(vertical: 12),      ),    );  }}