import 'package:flutter/cupertino.dart';import 'package:zen_player/modules/home/Home.dart';import 'package:zen_player/modules/search/SearchPage.dart';import 'package:zen_player/modules/setting/Setting.dart';typedef RouteFunction = Widget Function(BuildContext a);class Routes {  Routes() {    throw UnimplementedError(        "RoutesMap does not allow creating object. Just use static variables");  }  static const String home = "/";  static const String settings = "/settings";  static const String search = "/search";  static List<String> toList() {    return [home, settings];  }  static Map<String, WidgetBuilder> routesMap = {    home: (_) => Home(),    settings: (_) => Setting(),    search: (_) => SearchPage(),  };  static void changeRouteWithoutAnimation(    Widget page,    BuildContext context,  ) {    Navigator.push(        context,        PageRouteBuilder(          pageBuilder: (context, animation, secondaryAnimation) {            return page;          },          transitionDuration: Duration.zero,        ));  }}