import 'package:flutter_test/flutter_test.dart';import 'package:zen_player/counter.dart';void main() {  group("Counter class", () {    test("Value start at 0", () {      expect(Counter().value, 0);    });    test("Value should be incremented", () {      final Counter counter = Counter();      counter.increment();      expect(counter.value, 1);    });    test("Value should be decreased", () {      final Counter counter = Counter();      counter.decrement();      expect(counter.value, -1);    });  });}