import 'package:flutter/material.dart';import 'package:flutter_test/flutter_test.dart';import 'package:zen_player/redux/actions/app.dart';import 'package:zen_player/redux/actions/theme.dart';import 'package:zen_player/redux/reducers/app.dart';import 'package:zen_player/redux/reducers/theme.dart';import 'package:zen_player/redux/states/app.dart';void main() {  AppState appState = AppState();  setUp(() {    appState = AppState();  });  group("Test app reducers", () {    test("Test update view index", () {      final UpdateViewIndexAction action = UpdateViewIndexAction(1);      updateViewIndex(appState, action);      expect(appState.currentViewIndex, action.index);    });  });  group("Test theme reducers", () {    test("Test update thememode success", () {      final UpdateThemeModeAction action =          UpdateThemeModeAction(themeMode: ThemeMode.light);      updateThemeMode(appState, action);      expect(appState.themeState!.mode, ThemeMode.light);    });  });}