import 'dart:developer';import 'package:flutter/material.dart';import 'package:flutter_test/flutter_test.dart';import 'package:shared_preferences/shared_preferences.dart';import 'package:zen_player/modules/home/theme_manager.dart';void main() {  group("ThemeManager", () {    SharedPreferences? preferences;    setUp(() async {      preferences = await SharedPreferences.getInstance();      preferences!.remove(ThemeManager.THEME_KEY);    });    test("ThemeManager.updateTheme", () async {      await ThemeManager.updateTheme(ThemeMode.dark);      expect(preferences!.get(ThemeManager.THEME_KEY), ThemeMode.dark.index);    });    test("ThemeManager.getTheme", () async {      ThemeMode actualTheme = ThemeMode.light;      await ThemeManager.updateTheme(actualTheme);      ThemeMode? themeModeFromPref = await ThemeManager.getTheme();      expect(actualTheme, themeModeFromPref);    });    test("ThemeManager.removeTheme", () async {      await ThemeManager.updateTheme(ThemeMode.dark);      await ThemeManager.removeTheme();      expect(await ThemeManager.getTheme(), null);    });  });}